				serverOptions
						.setSsl(true)
						.setPemKeyCertOptions(
								new PemKeyCertOptions().
											setKeyPath(sslConfig.getKey()).
											setCertPath(sslConfig.getCert())));

		final StaticHandler staticHandler = StaticHandler.create();
